    private static int compareTimeString(String one, String two)
    {
        if (one != null && two != null)
        {
            int retVal = one.compareTo(two);

            if (retVal > 0)
                return 1; // one is greatet than two
            else if (retVal == 0) // both dates are equal
                return 0;

        }
        return -1; // one is less than two
    }
